import React, { Component } from "react";

class Card extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imagen: props.imagen,
            nombre: props.nombre,
            descripcion: props.descripcion,
            verMas: false,
            textoBoton: "Ver Mas"
        };
    }
cambiarVerMas(){

if (this.state.verMas){
    this.setState({
        verMas: false,
        textoBoton: "Ver Mas"
    })
}
else{
    this.setState({
        verMas: true,
        textoBoton: "Ver Menos"}) 
}


}

seleccionar = () => {
    this.setState(prevState => ({
        seleccionado: !prevState.seleccionado
    }));
}

    render() {
        return (
            <div className={this.state.seleccionado ? "active" : null}
            onDoubleClick={() => this.seleccionar()}>



                <img src={this.state.imagen} alt={this.state.nombre} />
                <h4>{this.state.nombre}</h4>
                <p>{this.state.verMas ? this.state.descripcion : null}</p>
                <button onClick={() => this.cambiarVerMas()}> {this.state.textoBoton} </button>
            </div>
        );
    }
}

export default Card;
