import React, { Component } from "react";

class Card extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imagen: props.imagen,
            nombre: props.nombre,
            descripcion: props.descripcion,
            verMas: false,
        };
    }

    seccionVerMas = () => {
        this.setState((prevState) => ({
            verMas: !prevState.verMas,
        }));
    };

    render() {
        const { imagen, nombre, descripcion, verMas } = this.state;
        return (
            <div className="character-card">
                <img src={imagen} alt={nombre} />
                <h4>{nombre}</h4>
                <p>{descripcion}</p>
                {verMas && (
                    <div className="informacion-adicional">
                        <p>Aquí va la información adicional que se mostrará cuando el usuario haga clic en "ver más".</p>
                    </div>
                )}
                <button onClick={this.seccionver}>
                    {verMas ? "Ver menos" : "Ver más"}
                </button>
            </div>
        );
    }
}

export default Card;
